var utility = require('utlity.js');

exports.returnUserInfo = function(req, res){
	var uid = req.body.uid;
	var queryString = "select * from d where personid='" + uid +"'";
	var client = utility.prepareDb();
	client.query(queryString, function(error, d){
		if(error){
			throw error;
		}
		res.send(d);
		client.end();
	});
};

exports.userGender = function(req, res){
	var username = req.body.username;
	var queryString = "select gender from d where account='" + username +"'";
	var client = utility.prepareDb();
	client.query(queryString, function(error, d){
		if(error){
			throw error;
		}
		res.send(d);
		client.end();
	});
};


